import { Component, OnInit } from '@angular/core';

import { HttpService } from '../http.service';
//import { Movie } from '../movie';
import { MovieService } from '../movie.service';
import {HttpClient} from "@angular/common/http";
import {Observable} from "rxjs";
import {environment} from "../environments/environment";

@Component({
  selector: 'app-movies',
  templateUrl: './moviesonline.component.html',
  styleUrls: ['./moviesonline.component.css']
})
export class MoviesonlineComponent implements OnInit {


  constructor(private movieService: HttpService) { }
 // constructor(private httpService: HttpService) { }

  ngOnInit(): void {
    this.getMovies();
  }

  getMovies(): void {
    this.movieService.getTrending();
  }

  getGenres(): Observable<any> {

    return this.movieService.getGenres();
  }

  /*

