    const url = `http://localhost:3000/${username}/${id}`;

    return this.httpClient.delete(url);
  }

  getGenres(): Observable<any> {
    const url = `https://api.themoviedb.org/3/genre/movie/list?api_key=${environment.api_key}&language=en-US`;

    return this.httpClient.get(url);
  }
*/

}


<h2>My Online Movies</h2>

<div>
  <label for="new-movie">Movie name: </label>
  <input id="new-movie" #movieName />
  
  <button type="button" class="add-button" (click)="getGenres(); ">
    Add movie
  </button>
</div>
